sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	// Add your code here
	gs.info('testUsers: ' + current.name);
	var arrGroups = [];
    var getMyGroups = new GlideRecord('sys_user_grmember');
    getMyGroups.addEncodedQuery('GOTOuser.nameLIKE' + current.name);
    getMyGroups.query();
    while (getMyGroups.next()) {
        arrGroups.push(getMyGroups.group.name.toString());
		gs.info('testUsers: ' + arrGroups);
    }

	var getUsername = current.user_name;
    var grNeedIts = new GlideRecord('x_58872_needit_needit');
    grNeedIts.addEncodedQuery('sys_created_by=' + getUsername);
    grNeedIts.query();
    while (grNeedIts.next()) {
        grNeedIts.setValue('groups_of_creator', arrGroups);
		grNeedIts.update();
    }

//     var updateTables = new GlideRecord('x_58872_needit_needit');
// 	updateTables.get(current.sys_id);
//     updateTables.setValue('groups_of_creator', arrGroups);
// 	updateTables.update();

})(current, previous);
