sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    // Add your code here
    var getCreateBy = current.sys_created_by;
    var grUsers = new GlideRecord('sys_user');
    grUsers.addEncodedQuery('user_nameSTARTSWITH' + getCreateBy);
    grUsers.query();
    while (grUsers.next()) {
        var getFullName = grUsers.name.toString();
    }

    var arrGroups = [];
    var getMyGroups = new GlideRecord('sys_user_grmember');
    getMyGroups.addEncodedQuery('GOTOuser.nameLIKE' + getFullName);
    getMyGroups.query();
    while (getMyGroups.next()) {
        arrGroups.push(getMyGroups.group.name.toString());
    }
    var updateTables = new GlideRecord('x_58872_needit_needit');
	updateTables.get(current.sys_id);
    updateTables.setValue('groups_of_creator', arrGroups);
	updateTables.update();

})(current, previous);
