sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
	var getEmail = g_form.getValue('email');
   g_form.hideErrorBox('email');
   return validateEmail(getEmail);
}

function validateEmail(email) { 
		if (!email || email.trim().length <= 0)
			return true;
		var re = /^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
		if (!re.test(email)) {
			g_form.showErrorBox('email', getMessage('Invalid Email'))
			return false;
		}
		return true;
	}
