sys_script_include.script:
//Check input date contains 8 digit with format(YYYYmmDD)
var CheckFormatValidateDate = Class.create();
CheckFormatValidateDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    CheckDate: function() {
		var date = this.getParameter('sysparm_date_string');
        if (date.match(/(19|20)\d{2}(0[1-9]|1[0-2])(0[1-9]|[12]\d|3[01])/)) {
			return 'true';
        } else {
            return 'false';
        }
    },
    type: 'CheckFormatValidateDate'
});
