sys_script_include.script:
var trees = [];
function GetTreeUsers() {
    var gr = new GlideRecord('sys_user');
    gr.get(gs.getUserID());
	trees.push(' '+ gr.department.name);
    foreachTrees(gr.department.parent);
    return trees + '';
}

function foreachTrees(name) {
    var grDpm = new GlideRecord('cmn_department');
    grDpm.addQuery('sys_id', name);
    grDpm.query();
    while (grDpm.next()) {
        trees.push(' ' + grDpm.name);
        foreachTrees(grDpm.parent);
    }
}
