sys_script_include.script:
var FilterMyGroups = Class.create();
FilterMyGroups.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
	getGroups: function() {
		var arrGroups = [];
		var fullName = this.getParameter('sysparm_full_name');
		var gr = new GlideRecord('sys_user_grmember');
		gr.addEncodedQuery('GOTOuser.nameLIKE' + fullName);
		gr.query();

		while(gr.next()){
			arrGroups.push(gr.group.toString());
		}

		return arrGroups + '';

	},
	type: 'FilterMyGroups'
});
